import { useState, useEffect } from 'react';

function Loading() {
    const [dots, setDots] = useState(0);
  
    useEffect(() => {
      const interval = setInterval(() => {
        setDots((dots) => (dots + 1) % 4);
      }, 500);
  
      return () => clearInterval(interval);
    }, []);
    return (
      <div className="loading-container">
        <img src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/029b8bd9-cb5a-41e4-9c7e-ee516face9bb/dayo3ow-7ac86c31-8b2b-4810-89f2-e6134caf1f2d.gif?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..ooubhxjHp9PIMhVxvCFHziI6pxDAS8glXPWenUeomWs" alt="pokeball" className="loading-image" />
        <h1 className="loading-title">Loading{'.'.repeat(dots)}</h1>
      </div>
    );
}

export default Loading